@app.post('/chat')
async def chat(request: Request):
    body = await request.json()
    message = body.get('message', '').strip()

    if message.lower().startswith('list '):
        result = file_access.list_dir(message.split('list ',1)[1].strip())
        return {'reply': format_response(result)}

    if message.lower().startswith('read '):
        result = file_access.safe_read(message.split('read ',1)[1].strip())
        return {'reply': format_response(result)}

    if message.lower().startswith('write '):
        if ':' in message:
            path, content = message.split(':',1)
            result = file_access.safe_write(path.replace('write ','').strip(), content.strip())
            return {'reply': format_response(result)}
        return {'reply': format_response({'status':'error','error':'Use format: write path: content'})}

    if message.lower().startswith('confirm overwrite '):
        path = message.replace('confirm overwrite ','').strip()
        result = file_access.safe_write(path,'',confirm=True)
        return {'reply': format_response(result)}

    reply = reasoning_core.think(message)
    return {'reply': format_response(reply)}
